import networkx as nx
import matplotlib.pyplot as plt


def _permutation_to_edges(perm):
    return [(x, y) for x, y in enumerate(perm)]


def draw_graph(permutations):
    G = nx.DiGraph()
    G.add_nodes_from(list(range(len(permutations[0]))))
    pos = nx.circular_layout(G)
    G.add_edges_from(_permutation_to_edges(permutations[0]))
    nx.draw_networkx_nodes(G, pos, cmap=plt.get_cmap('jet'),
                           node_color=(0.8, 0.8, 0.8), node_size=1000)
    nx.draw_networkx_labels(G, pos, node_size=1000)
    nx.draw_networkx_edges(G, pos, edgelist=_permutation_to_edges(permutations[0]),
                           node_size=1000)
    nx.draw_networkx_edges(G, pos, edgelist=_permutation_to_edges(permutations[1]),
                           node_size=1000, edge_color=[(1.0, 0.0, 0.0) for _ in G.edges()])
    plt.show()


